exports.getNode = function(node, callback){
    if (node){
        model.find({_id:node.nodeID}, function(err, node){
            if(err)
                callback({error:err}, undefined);
            else                       
                callback(undefined, node);
        });
    } else
        callback({error:'Gimme something to work with'}, undefined);      
};

