exports.removeNode = function(node, callback){
    if (node){
        model.remove ({_id:node.nodeID}, function (err, data){
            if (err)
                callback ({error:err}, undefined);
            else
                callback(undefined, data);
        });
    } else
        callback({error:'Gimme something to work with'}, undefined);  
};