//  The purpose of this module is to store the user's current settings
//******************************************************************************

var mongoose = require('mongoose');
var conn = require('d4-realmconnection');
var node = require('d4-realmnode');

var weaponTypes = ['Rock','Scissors','Paper'];
var actionTypes = ['Attack', 'Connect', 'Cut'];
var schema = new mongoose.Schema({
    user: {type: mongoose.Schema.Types.ObjectId, ref: 'user'}, 
    
    nodes: [{nodeid: mongoose.Schema.Types.ObjectId, 
             weapon: {type: String, enum: weaponTypes}}],

    actions: [{actiontype: {type: String, enum:actionTypes},
               target: mongoose.Schema.Types.ObjectId}]
    
});
var model = mongoose.model('realmUser', schema);
exports.schema = schema;
exports.model = model;