var result = require('d4-roshamresult');
var userObject = require('d4-user');
var nodemailer = require("nodemailer");
var sendmailTransport = require('nodemailer-sendmail-transport');
var mailOptions={
    from: "BiteMeFanBoy@d4-ideas.com",
    to: "",
    subject: "The Lord of Battles Has a New Result For You",
    text: "Dude, you've got, like, a new turn, and stuff. Or something, I dunno. Head over to http://d4-ideas.com/roshambattle"
};

exports.sendResults = function(theData){
    for (thisUser in theData.participants){
        if (theData.participants.hasOwnProperty(thisUser)){
            if (typeof theData.participants[thisUser].userid !== 'undefined'){
                console.log('thisUser userid');
                console.log(theData.participants[thisUser].userid);
                userObject.getUser({_id: theData.participants[thisUser].userid}, function(error, userData){
                    var transporter = nodemailer.createTransport(sendmailTransport());
                    if (typeof error !== 'undefined'){
                        console.log('Danger Will Robinson!');
                        console.log(error);
                    }
                    else {
                        mailOptions.to=userData.email;
                        transporter.sendMail(mailOptions, function(error, response){  //callback
                            if (error) {
                                console.log('Problem sending mail - ignore this on dev');
                                console.log(error);
                            }
                            else {
                                console.log("Message sent: " + response.message);
                            }
                        });
                    }
                    transporter.close(); // shut down the connection pool, no more messages.  Comment this line out to continue sending emails.
                });
            }
        }
    }
}